package Test;

public class Pattern_check {

	public static void main(String[] args) {
		// TODO Auto-generated method 
		for(int row=1;row<=2;row++)
		{
			for(int col=1;col<row;col--)
			{
				System.out.print(" ");
			}
				System.out.print("* ");
		}
		System.out.println();
	}}


